SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW job_v (job, banco, ult_exec, prox_exec, dur_seg, status, falhas, owner, comando, parada) AS select job
      ,substr(priv_user,1,10) banco
      ,substr(last_date||' '||last_sec,1,18)  ULT_EXEC
      ,substr(next_date||' '||next_sec,1,18)  PROX_EXEC
      ,TOTAL_TIME/1000			      DUR_SEG
      ,BROKEN				      STATUS
      ,FAILURES				      FALHAS
      ,substr(schema_user,1,10)               OWNER
      ,substr(what,1,128)               COMANDO
	, broken	PARADA
FROM user_jobs
order by 4 desc;

