CREATE OR REPLACE VIEW kv_form_stamp_v (cd_cgc, ds_razao_social, ds_endereco, ds_municipio, nr_telefone, ds_endereco_completo) AS select 	'260500005' cd_cgc,
		a.ds_razao_social,
		a.ds_endereco || ' ' || a.nr_endereco ds_endereco,
		a.cd_cep || ' ' || a.ds_municipio ds_municipio,
		'Tel. 0' || a.nr_telefone nr_telefone,
		a.cd_cep || ' ' || a.ds_municipio || ', ' || a.ds_endereco || ' ' || a.nr_endereco ds_endereco_completo
FROM 	pessoa_juridica a,
		estabelecimento b
where 	b.cd_estabelecimento = obter_estabelecimento_ativo
and 	a.cd_cgc = b.cd_cgc;

