CREATE OR REPLACE VIEW lab_repeticao_exame_v (nr_prescricao, nr_repeticao, nr_seq_exame, cd_procedimento, ds_procedimento, nm_exame, dt_prescricao) AS SELECT DISTINCT a.nr_prescricao, lab_nr_repeticoes_exame(a.nr_prescricao, a.nr_sequencia) nr_repeticao,
	   a.nr_seq_exame,
	   a.cd_procedimento,
	   SUBSTR(obter_desc_procedimento(a.cd_procedimento, a.ie_origem_proced),1,200) ds_procedimento,
	   SUBSTR(lab_obter_dados_exame(a.nr_seq_exame, 'N'),1,100)nm_exame,
	   c.dt_prescricao
FROM prescr_procedimento a,
	 prescr_proc_exame_repet b,
	 prescr_medica c
WHERE a.nr_prescricao = b.nr_prescricao
AND	  a.nr_sequencia = b.nr_seq_prescr
AND	  c.nr_prescricao = a.nr_prescricao
AND	  a.nr_seq_exame IS NOT NULL;

