CREATE OR REPLACE VIEW last_customer_environment_v (nm_database, ds_server_host, ie_production, ds_environment, dt_atualizacao) AS select	tce.nm_database,
			tce.ds_server_host,
			tce.ie_production,
			tce.ds_environment,
			tce.dt_atualizacao
	FROM (select	b.nm_database,
					b.ds_server_host,
					b.ie_production,
					b.ds_environment,
					b.dt_atualizacao
			 from	tasy_customer_environment b
