SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW library_lock_v (sid, username, status, osuser, machine, terminal, program, type, ds_form, nm_usuario, lockwait, logon_time, status_lock, "serial#") AS select distinct
	a.SID,
	a.USERNAME,
	a.STATUS,
	a.OSUSER,
	a.MACHINE,
	a.TERMINAL,
	a.PROGRAM,
	a.TYPE,
	a.MODULE ds_form,
	a.ACTION nm_usuario,
	a.LOCKWAIT,
	a.LOGON_TIME,
	CASE WHEN x.block=0 THEN  'Esperando'  ELSE 'Bloqueando' END  Status_lock,
	serial#
FROM 	v_$session a,
	v_$lock x
where	x.sid				= a.sid
and (x.request > 0 OR x.block > 0);

