SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW man_ordem_ativ_etapa_v (nr_sequencia, ds_atividade, nr_seq_ordem, pr_etapa, nr_seq_projeto, nr_seq_classif) AS select	a.nr_sequencia, /* Desenvolvimento */
	a.ds_atividade,
	d.nr_seq_ordem,
	coalesce(a.pr_etapa,0) pr_etapa,
	c.nr_sequencia nr_seq_projeto,
	c.nr_seq_classif
FROM	proj_ordem_servico d,
	proj_projeto c,
	proj_cronograma b,
	proj_cron_etapa a
where	a.nr_seq_cronograma = b.nr_sequencia
and	b.nr_seq_proj = c.nr_sequencia
and	c.nr_sequencia = d.nr_seq_proj
and	c.ie_origem = 'D'
and	coalesce(a.ie_fase,'N') = 'N'
and	coalesce(a.ie_situacao,'A') = 'A'
and	coalesce(b.ie_situacao,'A') = 'A'
and	coalesce(c.nr_seq_classif,0) <> 14
and	not exists (
		select	1
		from	proj_projeto x
		where	x.nr_seq_ordem_serv = d.nr_seq_ordem
		and	coalesce(x.nr_seq_classif,0) = 14)

union

select	a.nr_sequencia, /* Desenvolvimento */
	a.ds_atividade,
	c.nr_seq_ordem_serv,
	coalesce(a.pr_etapa,0) pr_etapa,
	c.nr_sequencia nr_seq_projeto,
	c.nr_seq_classif
from 	proj_projeto c,
	proj_cronograma b,
	proj_cron_etapa a
where	a.nr_seq_cronograma = b.nr_sequencia
and	b.nr_seq_proj = c.nr_sequencia
and	coalesce(a.ie_fase,'N') = 'N'
and	coalesce(a.ie_situacao,'A') = 'A'
and	coalesce(b.ie_situacao,'A') = 'A'
and	coalesce(c.nr_seq_classif,0) <> 14
and	not exists (
		select	1
		from	proj_projeto x
		where	x.nr_seq_ordem_serv = c.nr_seq_ordem_serv
		and	coalesce(x.nr_seq_classif,0) = 14)

union

select	a.nr_sequencia, /* Tecnologia */
	a.ds_atividade,
	d.nr_sequencia,
	coalesce(a.pr_etapa,0) pr_etapa,
	c.nr_sequencia nr_seq_projeto,
	c.nr_seq_classif
from 	man_ordem_servico d,
	proj_projeto c,
	proj_cronograma b,
	proj_cron_etapa a
where	a.nr_seq_cronograma = b.nr_sequencia
and	b.nr_seq_proj = c.nr_sequencia
and	d.nr_seq_proj_cron_etapa = a.nr_sequencia
and	coalesce(a.ie_fase,'N') = 'N'
and	coalesce(a.ie_situacao,'A') = 'A'
--and	nvl(c.nr_seq_classif,0) <> 14
and	not exists (
		select	1
		from	proj_projeto x
		where	x.nr_seq_ordem_serv = d.nr_sequencia
		and	coalesce(x.nr_seq_classif,0) = 14)

union

select	a.nr_sequencia, /* Migração - atividades normais/padrões do projeto */
	a.ds_atividade,
	c.nr_seq_ordem_serv,
	coalesce(a.pr_etapa,0) pr_etapa,
	c.nr_sequencia nr_seq_projeto,
	c.nr_seq_classif
from 	proj_projeto c,
	proj_cronograma b,
	proj_cron_etapa a
where	a.nr_seq_cronograma = b.nr_sequencia
and	b.nr_seq_proj = c.nr_sequencia
and	a.ie_tipo_obj_proj_migr <> 'T'
and	coalesce(a.ie_fase,'N') = 'N'
and	coalesce(a.ie_situacao,'A') = 'A'
and	coalesce(c.nr_seq_classif,0) = 14
--and	somente_nao_numero(a.ds_atividade) <> 'N'
and	not exists (
		select	1
		from	proj_cron_etapa x
		where	x.nr_seq_cronograma = b.nr_sequencia
		and	x.ie_fase = 'N'
		and	x.ie_tipo_obj_proj_migr in ('ATL_DES','ATL_TEC')
		and	coalesce(x.pr_etapa,0) <> 100)

union

select	a.nr_sequencia, /* Migração - atividades de atualização do projeto */
	a.ds_atividade,
	c.nr_seq_ordem_serv,
	coalesce(a.pr_etapa,0) pr_etapa,
	c.nr_sequencia nr_seq_projeto,
	c.nr_seq_classif
from 	proj_projeto c,
	proj_cronograma b,
	proj_cron_etapa a
where	a.nr_seq_cronograma = b.nr_sequencia
and	b.nr_seq_proj = c.nr_sequencia
and	a.ie_tipo_obj_proj_migr <> 'T'
and	coalesce(a.ie_fase,'N') = 'N'
and	coalesce(a.ie_situacao,'A') = 'A'
and	coalesce(c.nr_seq_classif,0) = 14
--and	somente_nao_numero(a.ds_atividade) <> 'N'
and	a.ie_tipo_obj_proj_migr in ('ATL_DES','ATL_TEC')
and	coalesce(a.pr_etapa,0) <> 100

union

select	a.nr_sequencia, /* Migração - atividades de testes do projeto */
	a.ds_atividade,
	c.nr_seq_ordem_serv,
	coalesce(a.pr_etapa,0) pr_etapa,
	c.nr_sequencia nr_seq_projeto,
	c.nr_seq_classif
from 	proj_projeto c,
	proj_cronograma b,
	proj_cron_etapa a
where	a.nr_seq_cronograma = b.nr_sequencia
and	b.nr_seq_proj = c.nr_sequencia
and	a.ie_tipo_obj_proj_migr = 'T'
and	coalesce(a.ie_fase,'N') = 'N'
and	coalesce(a.ie_situacao,'A') = 'A'
and	coalesce(c.nr_seq_classif,0) = 14
--and	somente_nao_numero(a.ds_atividade) <> 'N'
and	not exists (
		select	1
		from	cron_proj_migr_incon_v x
		where	x.nr_seq_cronograma = b.nr_sequencia
		and	x.ie_tipo_atividade <> 'T')
