CREATE OR REPLACE VIEW man_ordem_servico_dashboard_v (ds_classificacao, ds_satisfacao, ds_estagio, ds_os_encerrada_aut, qt_os_encerrada_aut, qt_total_os, nr_seq_cliente, dt_ordem_servico, ie_status_ordem, nr_seq_ordem_serv) AS select (
		select	coalesce(max(vd.ds_valor_dominio), 'N/A')
		FROM	valor_dominio_v vd
		where	vd.cd_dominio = 1149
		and	vd.vl_dominio = v.ie_classificacao
	) ds_classificacao,
	(
		select	vd.ds_valor_dominio
		from	valor_dominio_v vd
		where	vd.cd_dominio = 1197
		and	vd.vl_dominio = v.ie_grau_satisfacao
	) ds_satisfacao,
	(
		select	mep.ds_estagio
		from	man_estagio_processo mep
		where	mep.nr_sequencia = v.nr_seq_estagio
	) ds_estagio,
	obter_desc_exp_idioma(928166, wheb_usuario_pck.get_nr_seq_idioma) ds_os_encerrada_aut,
	(
		select	count(1)
		
		where	v.ie_status_ordem = 3
		and (v.nr_seq_tipo_solucao in (81) or v.nr_seq_estagio not in (9,511))
	) qt_os_encerrada_aut,
	1 qt_total_os,
	v.nr_seq_cliente,
	v.dt_ordem_servico,
	v.ie_status_ordem,
	v.nr_sequencia nr_seq_ordem_serv
from	man_ordem_servico v;

