SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW man_r_and_d_dashboard_sla_v (nr_sequencia, ds_periodo_os, dt_ordem_servico, ds_estagio, ds_dano_breve, ds_localizacao, ds_grupo_des, nm_usuario_executor, nr_seq_estagio, nr_seq_gerencia, nr_seq_status, ie_tipo_sla, qt_min_rest_pr_resp, qt_min_rest_solucao, qt_resp_cliente, ie_sla_leg, nr_atraso, ds_hr_rest_pr_resp, ds_hr_rest_solucao) AS select	os.NR_SEQUENCIA,os.DS_PERIODO_OS,os.DT_ORDEM_SERVICO,os.DS_ESTAGIO,os.DS_DANO_BREVE,os.DS_LOCALIZACAO,os.DS_GRUPO_DES,os.NM_USUARIO_EXECUTOR,os.NR_SEQ_ESTAGIO,os.NR_SEQ_GERENCIA,os.NR_SEQ_STATUS,os.IE_TIPO_SLA,os.QT_MIN_REST_PR_RESP,os.QT_MIN_REST_SOLUCAO,os.QT_RESP_CLIENTE,os.IE_SLA_LEG,
	CASE WHEN substr(ds_periodo_os,1,1)='G' THEN  1 WHEN substr(ds_periodo_os,1,1)='W' THEN  2 WHEN substr(ds_periodo_os,1,1)='C' THEN  3 WHEN substr(ds_periodo_os,1,1)='B' THEN  4 END  nr_atraso,
	man_obter_tempo_sla_formatado(os.qt_min_rest_pr_resp) ds_hr_rest_pr_resp,
	man_obter_tempo_sla_formatado(os.qt_min_rest_solucao) ds_hr_rest_solucao
FROM (	select	a.nr_sequencia,
			sla_dashboard_pck.obter_status_resp(a.nr_sequencia) ds_periodo_os,
			a.dt_ordem_servico,
			substr(a.ds_estagio, 1, 60) ds_estagio,
			substr(a.ds_dano_breve, 1, 80) ds_dano_breve,
			substr(a.ds_localizacao, 1, 50) ds_localizacao,
			substr(a.ds_grupo_des, 1, 60) ds_grupo_des,
			substr(obter_nome_usuario(man_obter_executor_ativo(a.nr_sequencia)), 1, 100) nm_usuario_executor,
			a.nr_seq_estagio,
			d.nr_seq_gerencia,
			b.nr_seq_status,
			coalesce(b.ie_tipo_sla, 998) ie_tipo_sla,
			sla_dashboard_pck.obter_min_primeiro_atend(a.nr_sequencia) qt_min_rest_pr_resp,
			sla_dashboard_pck.obter_min_solucao(a.nr_sequencia) qt_min_rest_solucao,
			CASE WHEN sla_dashboard_pck.obter_dt_primeiro_atend(a.nr_sequencia) IS NULL THEN  0  ELSE 1 END   qt_resp_cliente,
			man_obter_sla_leg(a.nr_sequencia) ie_sla_leg
		from	man_ordem_servico_v a,
			man_ordem_serv_sla b,
			man_estagio_processo mep,
			grupo_desenvolvimento d
		where	a.nr_sequencia = b.nr_seq_ordem
		and	a.nr_seq_estagio = mep.nr_sequencia
		and	a.nr_seq_grupo_des = d.nr_sequencia
		and (mep.ie_desenv = 'S' or mep.ie_tecnologia = 'S')
		and	ie_status_ordem <> 3
		and (ie_tipo_sla <> 3 or ie_tipo_sla_termino <> 3)
		
union all

		select	a.nr_sequencia,
			sla_dashboard_pck.obter_status_resp(a.nr_sequencia) ds_periodo_os,
			a.dt_ordem_servico,
			substr(a.ds_estagio, 1, 60) ds_estagio,
			substr(a.ds_dano_breve, 1, 80) ds_dano_breve, 
			substr(a.ds_localizacao, 1, 50) ds_localizacao,
			substr(a.ds_grupo_des, 1, 60) ds_grupo_des,
			substr(obter_nome_usuario(man_obter_executor_ativo(a.nr_sequencia)), 1, 100) nm_usuario_executor, 
			a.nr_seq_estagio, 
			d.nr_seq_gerencia,
			null,
			999,
			sla_dashboard_pck.obter_min_primeiro_atend(a.nr_sequencia) qt_min_rest_pr_resp,
			sla_dashboard_pck.obter_min_solucao(a.nr_sequencia) qt_min_rest_solucao, 
			CASE WHEN sla_dashboard_pck.obter_dt_primeiro_atend(a.nr_sequencia) IS NULL THEN  0  ELSE 1 END  qt_resp_cliente,
			man_obter_sla_leg(a.nr_sequencia) ie_sla_leg
		from	man_ordem_servico_v a,
			grupo_desenvolvimento d
		where	a.nr_seq_grupo_des = d.nr_sequencia 
		and	a.ie_classificacao = 'E' 
		and	not exists (	select	1 
					from	man_ordem_serv_sla b 
					where	a.nr_sequencia = b.nr_seq_ordem
				)
		and	obter_se_os_pend_cliente(a.nr_sequencia) = 'N'
		and	man_obter_se_estagio_desenv(a.nr_seq_estagio) = 'S'
		and	ie_status_ordem <> 3
	)	os
where	os.nr_seq_status > 0
order by	os.ds_periodo_os;

