CREATE OR REPLACE VIEW material_comprador_v (cd_material, cd_comprador, nm_comprador, cd_estabelecimento) AS select  a.cd_material,
	obter_comprador_material(a.cd_material,'T',b.cd_estabelecimento) cd_comprador,
        obter_usuario_pf(obter_comprador_material(a.cd_material,'T',b.cd_estabelecimento)) nm_comprador,
        b.cd_estabelecimento
FROM    material a,
        material_estab b
where   b.cd_material = a.cd_material
and     a.ie_situacao = 'A';

