CREATE OR REPLACE VIEW material_estoque_devolucao_v (opcao, nr_movimento_estoque, nr_atendimento, nr_seq_lote_fornec, cd_fornecedor, cd_local_estoque, cd_material, cd_setor_atendimento, dt_atendimento, cd_unidade_med_mov, ds_material, qt_movimento, qt_estoque, cd_estabelecimento, dt_mesano_referencia) AS select	1 opcao,
		a.nr_movimento_estoque,
		a.nr_atendimento,
		a.nr_seq_lote_fornec,
		a.cd_fornecedor,
		a.cd_local_estoque,
		a.cd_material,
		a.cd_setor_atendimento,
		a.dt_movimento_estoque dt_atendimento,
		a.cd_unidade_med_mov,
		substr(obter_desc_material(a.cd_material),1,255) ds_material,
		a.qt_movimento,
		a.qt_estoque,
		a.cd_estabelecimento,
		a.dt_mesano_referencia
FROM	movimento_estoque a
where	a.cd_local_estoque is not null
--and 	a.nr_prescricao > 0
--and 	exists (select 1 from ap_lote l where l.nr_sequencia = a.nr_lote_ap and ie_status_lote = 'A')
and 	not exists (select 	1
					from 	material_atend_paciente m
					where	a.nr_atendimento 		= m.nr_atendimento
					and 	a.cd_setor_atendimento 	= m.cd_setor_atendimento
					and 	m.nr_seq_lote_ap 		= a.nr_lote_ap)

union all

select 	2 opcao,
		b.nr_sequencia,
		c.nr_atendimento,
		b.nr_seq_lote_fornec,
		b.cd_cgc_fornec,
		b.cd_local_estoque,
		b.cd_material,
		c.cd_setor_atendimento,
		b.dt_atendimento,
		b.cd_unidade_medida,
		substr(obter_desc_material(b.cd_material),1,255) ds_material,
		(b.qt_material * -1) qt_movimento,
		(b.qt_material_estoque * -1) qt_estoque,
		null,
		null
from	farm_devolucao_mov_item b,
		farm_devolucao_movimento c
where 	b.nr_seq_devolucao = c.nr_sequencia
and 	c.dt_baixa is null
and 	b.dt_recebimento is null;

