CREATE OR REPLACE VIEW meurh_v (cd_funcionario, nm_funcionario, cd_cargo, ds_cargo, vl_hora, ie_sexo, dt_referencia, qt_horas, ds_sexo, vl_salario) AS select b.cd_funcionario,b.nm_funcionario,a.cd_cargo,a.ds_cargo,
	a.vl_salario vl_hora,b.ie_sexo,c.dt_referencia,c.qt_horas,
	CASE WHEN b.ie_sexo='M' THEN 'Masculino'  ELSE 'Feminino' END  ds_sexo,
	(c.qt_horas * a.vl_salario) vl_salario
FROM rh_cargo a,
     rh_funcionario b,
     rh_ponto c
where b.cd_cargo = a.cd_cargo
and b.cd_funcionario = c.cd_funcionario;

