CREATE OR REPLACE VIEW nota_interf_giss_pj_v (nr_nota_fiscal, cd_serie_nf, dt_emissao, ie_situacao, vl_total_nota, ie_tomador_municipio, ds_razao_social, nr_inscricao_municipal, cd_cnpj, ie_tomador_isento, nr_inscricao_estadual, cd_cep, ds_tipo_logradouro, ds_titulo_logradouro, ds_endereco, ds_complemento, nr_endereco, ds_bairro, sg_estado, ds_municipio, ds_local_prest_servico, cd_estabelecimento, cd_atividade_serv, cd_operacao_nf) AS select	a.nr_nota_fiscal, -- afstringari 209328 04/10/2010 
