	somente_numero(coalesce(substr(obter_dados_pf_pj(null, a.cd_cgc, 'IE'),1,20), '00000000000000000000')) nr_inscricao_estadual, 
	substr(replace(obter_dados_pf_pj(null, a.cd_cgc, 'CEP'),'-',''),1,8) cd_cep, 
	'Rua' ds_tipo_logradouro, 
	' ' ds_titulo_logradouro, 
	substr(obter_dados_pf_pj(null, a.cd_cgc, 'EN'),1,50) ds_endereco, 
