SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW objeto_compilar_v (nm_objeto, nr_ordem, ie_tipo_objeto, ds_script_criacao, dt_atualizacao) AS SELECT substr(nm_objeto,1,40) nm_objeto,
	obter_ordem_Objeto(ie_tipo_objeto) nr_ordem,
	ie_tipo_objeto,
	ds_script_criacao,
	a.dt_atualizacao
FROM objeto_sistema a
LEFT OUTER JOIN user_objects b ON (a.nm_objeto = b.object_name)
WHERE upper(ie_tipo_objeto) IN ('PROCEDURE', 'VIEW', 'TRIGGER',
			'PACKAGE', 'PACKAGE BODY','FUNCTION') and nm_objeto	<> 'TASY_CRIAR_OBJETO_SISTEMA'  and to_date(coalesce(timestamp,'2000-01-01:00:00:01'),'yyyy-mm-dd:hh24:mi:ss') < a.dt_atualizacao;

