CREATE OR REPLACE VIEW oft_agenda_exames_v (ds_agenda, nr_minuto_duracao, nm_paciente_pf, nr_atendimento, ds_convenio, nm_medico, qt_min_espera_tasy, dt_entrada_tasy, cd_pessoa_fisica, ds_observacao, ds_status_paciente, ds_protocolo, ds_cirurgia, dt_agenda, cd_procedimento, nr_sequencia, ie_origem_proced, nr_seq_proc_interno, nm_usuario_acesso, nr_seq_status_pac, cd_tipo_agenda, ie_situacao, cd_estabelecimento, cd_medico_exec, cd_turno, ie_status_agenda, nm_paciente, hr_inicio, cd_agenda, ie_status_laudo, dt_confirmacao, ie_autorizacao, ie_encaixe, ds_precaucao, nr_seq_horario, nr_seq_esp, nr_seq_oftalmo, dt_inicio_consulta, nr_seq_classif_agenda) AS select
	a.ds_agenda, 
	b.nr_minuto_duracao, 
	substr(CASE WHEN b.ie_status_agenda='B' THEN CASE WHEN b.ds_observacao IS NULL THEN  obter_desc_expressao(302715,'Bloqueado')  ELSE obter_desc_expressao(302715,'Bloqueado')||'('|| b.ds_observacao || ')' END   ELSE coalesce(substr(obter_nome_pf(b.cd_pessoa_fisica),1,200),substr(b.nm_paciente,1,200)) END ,1,200) nm_paciente_pf, 
	b.nr_atendimento, 
	substr(obter_nome_convenio(b.cd_convenio),1,40) ds_convenio, 
	substr(obter_nome_pf(b.cd_medico),1,200) nm_medico, 
	Obter_Tempo_Espera_Atend(b.nr_atendimento) qt_min_espera_tasy,
	obter_data_entrada(b.nr_atendimento) dt_entrada_tasy, 
	b.cd_pessoa_fisica, 
	substr(b.ds_observacao,1,250) ds_observacao, 
	s.ds_status_paciente,
	b.ds_protocolo, 
	substr(b.ds_cirurgia,1,250) ds_cirurgia,
	b.dt_agenda,
	b.cd_procedimento,
	b.nr_sequencia, 
	b.ie_origem_proced,
	b.nr_seq_proc_interno,
	b.nm_usuario_acesso,
	b.nr_seq_status_pac, 
	a.cd_tipo_agenda, 
