	b.hr_inicio, 
	a.cd_agenda,
	substr(coalesce(obter_status_laudo_agenda(b.nr_sequencia, b.nr_atendimento),'N'),1,3) ie_status_laudo,
	b.dt_confirmacao,
	b.ie_autorizacao,
	b.ie_encaixe,
	substr(obter_desc_precaucao_agenda(b.nr_atendimento,b.cd_pessoa_fisica),1,255) ds_precaucao,
	b.nr_seq_horario,
	b.nr_seq_esp,
	b.NR_SEQ_OFTALMO,
   d.dt_inicio_consulta,
   b.nr_seq_classif_agenda
FROM agenda a, agenda_paciente b
LEFT OUTER JOIN status_paciente_agenda s ON (b.nr_seq_status_pac = s.nr_sequencia)
LEFT OUTER JOIN oft_consulta d ON (b.nr_seq_oftalmo = d.nr_sequencia)
WHERE a.cd_agenda = b.cd_agenda   and coalesce(a.ie_situacao,'A') = 'A' and cd_tipo_agenda = 2;

