CREATE OR REPLACE VIEW os_atual_proj_migr_v (ie_tipo_atualizacao, nr_seq_projeto, nr_seq_os, dt_estagio_os, ds_documentacao) AS select	'T' ie_tipo_atualizacao,
	nr_seq_projeto,
	nr_seq_os,
	dt_estagio_os,
	null ds_documentacao
FROM	os_nec_vinc_proj_migr_v
where	nr_seq_estagio_os in (2,41,1511)

union all

select	'D' ie_tipo_atualizacao,
	p.nr_sequencia nr_seq_projeto,
	w.nr_seq_os nr_seq_os,
	w.dt_atualizacao_nrec dt_estagio_os,
	w.ds_documentacao ds_documentacao
from	proj_projeto p,
	w_migracao_ordem_servico w
where	p.cd_funcao = w.cd_funcao
and	p.nr_seq_gerencia = 9
and	p.nr_seq_classif = 14
and	w.ie_tipo_atualizacao = 'C';

