SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW os_recebida_html5_v (nr_sequencia, nr_seq_gerencia, dt_ordem_servico, nr_seq_grupo_des, nr_seq_localizacao, cd_funcao, ie_plataforma) AS SELECT 	a.nr_sequencia,
		h.nr_seq_gerencia,
		a.dt_ordem_servico,
		a.nr_seq_grupo_des,
		a.nr_seq_localizacao,
		a.cd_funcao,
		a.ie_plataforma
FROM    MAN_ORDEM_SERVICO a,
		MAN_ESTAGIO_PROCESSO b,
		gerencia_wheb g,
		grupo_desenvolvimento h
WHERE   a.NR_SEQ_ESTAGIO = b.nr_sequencia
AND		a.IE_PLATAFORMA = 'H'
--AND		a.dt_ordem_servico BETWEEN TO_DATE('01/03/2017', 'dd/mm/yyyy') AND TO_DATE('31/03/2017 23:59:59', 'dd/mm/yyyy hh24:mi:ss')
AND 	g.ie_area_gerencia IN ('DES','PDES')
AND		g.nr_sequencia = h.nr_seq_gerencia
AND 	h.nr_sequencia = a.nr_seq_grupo_des
AND 	g.nr_sequencia NOT IN (144, 136) --exclui gerencias de localização
AND	EXISTS ( SELECT 1
		FROM	man_estagio_processo c,
				man_ordem_serv_estagio d
		WHERE	c.nr_sequencia = d.nr_seq_estagio
		AND	d.nr_seq_ordem = a.nr_sequencia
		AND (c.ie_suporte = 'S' OR c.ie_desenv = 'S'));

