SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW painel_exames_realizados_v (ds_tipo_exame, cd_estabelecimento, qt_procedimento) AS select	/*+ INDEX (A PROPACI_I4) INDEX (Q CONPACI_PK)  */	SUBSTR(coalesce(obter_valor_dominio(95, p.cd_tipo_procedimento),'Não definido'),1,100) ds_tipo_exame,
	q.cd_estabelecimento,
	SUM(a.qt_procedimento) qt_procedimento
FROM 	procedimento p,
	conta_paciente q,
	procedimento_paciente a
where	a.cd_procedimento  = p.cd_procedimento
and	a.ie_origem_proced = p.ie_origem_proced
and	a.nr_interno_conta = q.nr_interno_conta
and 	a.dt_procedimento between TRUNC(LOCALTIMESTAMP,'month') and TRUNC(LOCALTIMESTAMP,'dd')
and 	a.cd_motivo_exc_conta is null
and	a.nr_sequencia <> coalesce(a.nr_seq_proc_pacote,0)
and 	a.vl_procedimento <> 0
group by SUBSTR(coalesce(obter_valor_dominio(95, p.cd_tipo_procedimento),'Não definido'),1,100),
	q.cd_estabelecimento;

