SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW pedido_exame_externo_item_v (dt_solicitacao, nm_profissional, ds_especialidade, ds_faixa_etaria, qt_exame) AS select	a.DT_SOLICITACAO,
	substr(obter_nome_pf(a.CD_PROFISSIONAL),1,80) nm_PROFISSIONAL, 
	substr(obter_especialidade_medico(a.cd_profissional,'D'),1,90) ds_especialidade, 
	substr(eis_obter_faixa_etaria(obter_idade_pf(a.cd_pessoa_fisica,LOCALTIMESTAMP,'A')),1,20) ds_faixa_etaria, 
	b.qt_exame 
FROM	PEDIDO_EXAME_EXTERNO a, 
	PEDIDO_EXAME_EXTERNO_ITEM b 
where	a.nr_sequencia	= b.NR_SEQ_PEDIDO;

