CREATE OR REPLACE VIEW pedido_medico_prescr_v (nr_sequencia, nr_prescricao, im_documento) AS SELECT	pd.nr_sequencia, pd.nr_prescricao, pdf.im_documento
FROM 	pessoa_documentacao pd,
		tipo_documentacao td,
		pessoa_doc_foto pdf
WHERE 	td.nr_sequencia = pd.nr_seq_documento
AND 	pdf.nr_seq_pessoa_doc = pd.nr_sequencia
AND 	td.ie_integra_pacs = 'S'
AND		pd.nr_prescricao IS NOT NULL
ORDER BY pd.nr_sequencia DESC, pdf.nr_sequencia DESC;

