CREATE OR REPLACE VIEW pfcs_dynamic_module_overview_v (nr_seq_display, nr_seq_dynamic, ds_module, pr_capacity, vl_census, vl_pred_census, qt_avail_beds, qt_pred_avail_beds, qt_blocked_beds, qt_discharge_orders, qt_discharge_delays, qt_pred_discharge, qt_total_admissions, qt_total_discharges, cd_establishment, ds_dynamic_parameters) AS select	d.nr_seq_display,
