CREATE OR REPLACE VIEW pix_cobranca_status_v (ds_status, ds_cor, ds_descricao, ordem) AS select ds_status, ds_cor, ds_descricao,
   case ds_status
   when 'NAO_REGISTRADA' then '0'
   when 'ATIVA' then '1'
   when 'CONCLUIDA' then '2'    
   when 'DEVOLVIDA' then '3'   
   when 'CANCELADA' then '4'   
   when 'REMOVIDA_PELO_USUARIO_RECEBEDOR' then '5' 
   when 'REMOVIDA_PELO_PSP' then '6'   
   end ordem
FROM (
	select distinct a.ds_status,
		   case ds_status
		   when 'NAO_REGISTRADA' then '#9E9E9E' 
		   when 'ATIVA' then '#E3A600' 	   
		   when 'CONCLUIDA' then '#00BA2F' 
		   when 'DEVOLVIDA' then '#E86C00'
		   when 'CANCELADA' then '#DA2935'	   
		   when 'REMOVIDA_PELO_USUARIO_RECEBEDOR' then '#DA2935' 
		   when 'REMOVIDA_PELO_PSP' then '#DA2935'
		   end ds_cor,
		   case ds_status
		   when 'NAO_REGISTRADA' then obter_desc_expressao(1062390) 
		   when 'ATIVA' then obter_desc_expressao(1062386 ) 	   
		   when 'CONCLUIDA' then obter_desc_expressao(297308)
		   when 'DEVOLVIDA' then obter_desc_expressao(312960) 
		   when 'CANCELADA' then obter_desc_expressao(302747)  	   
		   when 'REMOVIDA_PELO_USUARIO_RECEBEDOR' then obter_desc_expressao(1036828) 
		   when 'REMOVIDA_PELO_PSP' then obter_desc_expressao(1062388) 
		   end ds_descricao
	from pix_cobranca a) alias7
order by ordem;

