CREATE OR REPLACE VIEW pls_cp_cta_segurado_plano_v (nr_sequencia, nr_seq_contrato, nr_seq_plano, ie_preco, ie_tipo_contratacao, ie_acomodacao, nr_seq_tipo_acomodacao, nr_seq_categoria, nr_seq_grupo, ie_franquia) AS select	a.nr_sequencia,
