SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW pls_estrutura_material_v (nr_seq_estrutura, nr_seq_material, nr_seq_estrut_mat, nr_seq_estrut_superior, ie_tipo_despesa) AS select	a.nr_sequencia      nr_seq_estrutura,
	c.nr_sequencia      nr_seq_material,
	c.nr_seq_estrut_mat nr_seq_estrut_mat,
	a.nr_seq_superior   nr_seq_estrut_superior,
	c.ie_tipo_despesa   ie_tipo_despesa
FROM	pls_estrutura_material a,
	pls_material c
where	c.nr_seq_estrut_mat in (WITH RECURSIVE cte AS (
select 	z.nr_sequencia
				from 	pls_estrutura_material z WHERE z.nr_sequencia = a.nr_sequencia
  UNION ALL
select 	z.nr_sequencia
				from 	pls_estrutura_material z JOIN cte c ON (c.prior nr_sequencia = z.z.nr_seq_superior)

) SELECT * FROM cte;
);

