CREATE OR REPLACE VIEW pls_margem_mensalidade_seg_v (vl_mensalidade, vl_antecipacao, vl_outros, vl_coparticipacao_rembolso, dt_mesano_referencia, nr_seq_segurado) AS select	sum(d.vl_item) vl_mensalidade,
	0 vl_antecipacao,
	0 vl_outros,
	0 vl_coparticipacao_rembolso,
	c.dt_mesano_referencia dt_mesano_referencia,
	c.nr_seq_segurado nr_seq_segurado
FROM	pls_mensalidade_seg_item	d,
	pls_mensalidade_segurado	c,
	pls_mensalidade			b,
	pls_lote_mensalidade		a
where	d.nr_seq_mensalidade_seg	= c.nr_sequencia
and	c.nr_seq_mensalidade		= b.nr_sequencia
and	b.nr_seq_lote			= a.nr_sequencia
and	b.ie_cancelamento is null
and	a.ie_status	= 2
and	d.ie_tipo_item <> '11'
group by c.dt_mesano_referencia,c.nr_seq_segurado

UNION ALL

select	0 vl_mensalidade,
	sum(coalesce(d.vl_antecipacao,0)) vl_antecipacao,
	0 vl_outros,
	0 vl_coparticipacao_rembolso,
	d.dt_antecipacao dt_mesano_referencia,
	c.nr_seq_segurado nr_seq_segurado
from	pls_mensalidade_seg_item	d,
	pls_mensalidade_segurado	c,
	pls_mensalidade			b,
	pls_lote_mensalidade		a
where	d.nr_seq_mensalidade_seg	= c.nr_sequencia
and	c.nr_seq_mensalidade		= b.nr_sequencia
and	b.nr_seq_lote			= a.nr_sequencia
and	b.ie_cancelamento is null
and	a.ie_status	= 2
group by d.dt_antecipacao,c.nr_seq_segurado

UNION ALL

select	0 vl_mensalidade,
	0 vl_antecipacao,
	sum(coalesce(d.vl_antecipacao,0)) vl_outros,
	0 vl_coparticipacao_rembolso,
	c.dt_mesano_referencia dt_mesano_referencia,
	c.nr_seq_segurado nr_seq_segurado
from	pls_mensalidade_seg_item	d,
	pls_mensalidade_segurado	c,
	pls_mensalidade			b,
	pls_lote_mensalidade		a
where	d.nr_seq_mensalidade_seg	= c.nr_sequencia
and	c.nr_seq_mensalidade		= b.nr_sequencia
and	b.nr_seq_lote			= a.nr_sequencia
and	b.ie_cancelamento is null
and	a.dt_contabilizacao is null
and	a.ie_status	= 2
group by c.dt_mesano_referencia,c.nr_seq_segurado

union all

select	0 vl_mensalidade,
	0 vl_antecipacao,
	0 vl_outros,
	sum(coalesce(b.vl_coparticipacao,0)),
	a.dt_mes_competencia dt_mesano_referencia,
	b.nr_seq_segurado
from    pls_conta		b,
	pls_protocolo_conta	a
where	a.nr_sequencia	= b.nr_seq_protocolo
and	a.ie_status = '3'
and	a.ie_situacao in ('D','T')
and	a.ie_tipo_protocolo	= 'R'
group by	b.nr_seq_segurado,a.dt_mes_competencia;

