SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW pls_preco_grupo_mat_v (nr_seq_material, nr_seq_grupo) AS select	a.nr_sequencia nr_seq_material,
	ps.nr_seq_grupo
FROM	pls_preco_grupo_material gs,
	pls_preco_material ps,
	pls_material a
where	gs.nr_sequencia		= ps.nr_seq_grupo
and	gs.ie_situacao 		= 'A'
and	ps.nr_seq_material	= a.nr_sequencia

union all

select	a.nr_sequencia nr_seq_material,
	ps.nr_seq_grupo
from	pls_preco_grupo_material gs,
	pls_preco_material ps,
	pls_material a
where	gs.nr_sequencia		= ps.nr_seq_grupo
and	gs.ie_situacao 		= 'A'
and	ps.nr_seq_estrutura_mat	= a.nr_seq_estrut_mat;

