CREATE OR REPLACE VIEW pls_sca_v (nr_seq_segurado, nr_seq_contrato, nr_seq_pagador, nr_seq_plano, nr_seq_vinculo) AS select	a.nr_sequencia nr_seq_segurado,
	a.nr_seq_contrato,
	a.nr_seq_pagador,
	b.nr_seq_plano,
	b.nr_sequencia nr_seq_vinculo
FROM	pls_segurado a,
	pls_sca_vinculo b
where	((a.nr_sequencia	= b.nr_seq_segurado)
or (a.nr_seq_contrato	= b.nr_seq_contrato)
or (a.nr_seq_plano		= b.nr_seq_plano));

