CREATE OR REPLACE VIEW pls_segurado_conta_v (nr_sequencia, nr_seq_plano, cd_convenio, cd_categoria, qt_idade_anos, qt_idade_meses, ie_local_cadastro, ie_tipo_segurado, ie_pcmso, ie_sexo, ie_preco, nr_seq_contrato, nr_contrato, nr_seq_intercambio, ie_regulamentacao, dt_nascimento, dt_primeira_utilizacao, dt_contrato, dt_contratacao, nr_seq_titular, nr_seq_grupo_coop, cd_matricula_estipulante, ie_acomodacao, nr_seq_grupo_intercambio, nr_seq_congenere, ie_nascido_plano) AS select	a.nr_sequencia,
	a.nr_seq_plano, 
	pls_obter_conv_cat_segurado(a.nr_sequencia, 1) cd_convenio, 
	pls_obter_conv_cat_segurado(a.nr_sequencia, 2) cd_categoria, 
	obter_idade(b.dt_nascimento, LOCALTIMESTAMP, 'A') qt_idade_anos, 
	obter_idade(b.dt_nascimento, LOCALTIMESTAMP, 'M') qt_idade_meses, 
	a.ie_local_cadastro, 
	a.ie_tipo_segurado, 
	coalesce(a.ie_pcmso,'N') ie_pcmso, 
	b.ie_sexo, 
	c.ie_preco, 
	a.nr_seq_contrato, 
	d.nr_contrato, 
	a.nr_seq_intercambio, 
	c.ie_regulamentacao, 
	b.dt_nascimento, 
	(select	min(x.dt_atendimento) 
	FROM	pls_conta_ocor_v	x 
	where	x.nr_seq_segurado	= a.nr_sequencia) dt_primeira_utilizacao, 
	d.dt_contrato, 
	a.dt_contratacao, 
	a.nr_seq_titular, 
	a.nr_seq_grupo_coop, 
	a.cd_matricula_estipulante, 
	c.ie_acomodacao, 
	a.nr_seq_grupo_intercambio, 
	a.nr_seq_congenere, 
	a.ie_nascido_plano 
FROM pessoa_fisica b, pls_segurado a
LEFT OUTER JOIN pls_plano c ON (a.nr_seq_plano = c.nr_sequencia)
LEFT OUTER JOIN pls_contrato d ON (a.nr_seq_contrato = d.nr_sequencia)
WHERE b.cd_pessoa_fisica	= a.cd_pessoa_fisica;

