CREATE OR REPLACE VIEW pls_segurado_v (nr_seq_segurado, cd_pessoa_fisica, nm_segurado, cd_usuario_plano, nr_seq_titular, nr_seq_contrato, nr_seq_pagador, nr_seq_plano, nr_seq_tabela, nr_proposta_adesao, dt_adesao, dt_inclusao_operadora, dt_liberacao, dt_rescisao, ds_observacao, dt_reativacao, vl_analise, dt_nascimento, ie_sexo, ie_segmentacao_plano, ie_tipo_contratacao_plano, ie_preco_pre_pos_misto) AS select	a.nr_sequencia nr_seq_segurado,
	a.cd_pessoa_fisica, 
	substr(obter_nome_pf(a.cd_pessoa_fisica),1,255) nm_segurado, 
	substr(pls_obter_carteira_segurado(a.nr_sequencia),1,30) cd_usuario_plano, 
	a.nr_seq_titular, 
	b.nr_sequencia nr_seq_contrato, 
	a.nr_seq_pagador, 
	a.nr_seq_plano, 
	a.nr_seq_tabela, 
	a.nr_proposta_adesao, 
	a.dt_contratacao dt_adesao, 
	a.dt_inclusao_operadora, 
	a.dt_liberacao, 
	a.dt_rescisao, 
	substr(a.ds_observacao,1,255) ds_observacao, 
	a.dt_reativacao, 
	coalesce(a.vl_analise,0) vl_analise, 
	c.dt_nascimento, 
	c.ie_sexo, 
	(select	max(x.ie_segmentacao) 
	FROM	pls_plano x 
	where	x.nr_sequencia = a.nr_seq_plano) ie_segmentacao_plano, 
	(select	max(x.ie_tipo_contratacao) 
	from	pls_plano x 
	where	x.nr_sequencia = a.nr_seq_plano) ie_tipo_contratacao_plano, 
	-- Retorna a formação baseada no domínio 6961 para definir se Pré, Pós ou misto. 
	(select	case(x.ie_preco) 
		when '1' then 'PR' 
		when '2' then 'PO' 
		when '3' then 'PO' 
		when '4' then 'MI' 
		end 
	from	pls_plano x 
	where	x.nr_sequencia = a.nr_seq_plano) ie_preco_pre_pos_misto 
from	pls_contrato b, 
	pessoa_fisica c, 
	pls_segurado a 
where	b.nr_sequencia		= a.nr_seq_contrato 
and	a.cd_pessoa_fisica	= c.cd_pessoa_fisica;

