CREATE OR REPLACE VIEW pls_solic_alt_web_v (cd_pessoa_fisica, nm_pessoa_fisica, nr_seq_alteracao, dt_atualizacao_nrec, nm_usuario, ie_tipo_solicitacao, nr_seq_contrato) AS select substr(pls_obter_pessoa_chave_comp(a.nr_sequencia,'P'),1,255) cd_pessoa_fisica,
    substr(obter_nome_pf(pls_obter_pessoa_chave_comp(a.nr_sequencia,'P')),1,255) nm_pessoa_fisica, 
    b.nr_sequencia nr_seq_alteracao,	 
    to_char(b.dt_atualizacao_nrec,'dd/mm/yyyy hh:mi:ss') dt_atualizacao_nrec, 
    coalesce(b.nm_usuario_nrec,b.nm_usuario) nm_usuario, 
	b.ie_tipo_solicitacao, 
    b.nr_seq_contrato 
FROM  tasy_solic_alteracao	b, 
    tasy_solic_alt_campo	a    
where  a.nr_seq_solicitacao	= b.nr_sequencia 
and (b.ie_processo is null or b.ie_processo = 'W') 
and   b.dt_analise is null;

