	'5' ie_tipo
from	pls_protocolo_conta	a,
	pls_prestador 		c,
	pls_outorgante 		b
where	a.nr_seq_prestador	= c.nr_sequencia
and	a.nr_seq_outorgante	= b.nr_sequencia

union

select	distinct
	'SITUACAO_PROTOCOLO' ds_tipo_transacao,
	coalesce(a.dt_mes_competencia, LOCALTIMESTAMP) dt_transacao,
