CREATE OR REPLACE VIEW pls_vendedor_v (nr_seq_vendedor, cd_vendedor, nm_vendedor, cd_estabelecimento, ie_situacao, cd_sistema_anterior, nr_seq_classif_canal) AS select	a.nr_sequencia nr_seq_vendedor,
	b.cd_pessoa_fisica cd_vendedor,
	b.nm_pessoa_fisica nm_vendedor,
	a.cd_estabelecimento,
	a.ie_situacao	ie_situacao,
	a.cd_sistema_anterior,
	a.nr_seq_classif_canal
FROM	pls_vendedor a,
	pessoa_fisica b
where	a.cd_pessoa_fisica	= b.cd_pessoa_fisica
and	a.cd_cgc is null

union

select	a.nr_sequencia nr_seq_vendedor,
	b.cd_cgc cd_vendedor,
	b.ds_razao_social nm_vendedor,
	a.cd_estabelecimento,
	a.ie_situacao	ie_situacao,
	a.cd_sistema_anterior,
	a.nr_seq_classif_canal
from	pls_vendedor a,
	pessoa_juridica b
where	a.cd_cgc	= b.cd_cgc
and	a.cd_pessoa_fisica is null;

