CREATE OR REPLACE VIEW plussoft_dia_vencimento_v (nr_sequencia, dt_dia_vencimento, ie_tipo_contratacao, ie_situacao) AS select  a.nr_sequencia,
		a.dt_dia_vencimento,
		b.ie_tipo_contratacao,
		coalesce(a.ie_situacao,'A') ie_situacao
	 FROM	pls_regra_dia_vencimento     a,
		pls_regra_portador_mens    b
	where   a.nr_seq_regra_portador          = b.nr_sequencia
	order by dt_dia_vencimento;

