CREATE OR REPLACE VIEW plussoft_faixa_etaria_v (nr_seq_faixa_etaria, nr_seq_faixa_item, ds_faixa_etaria, qt_idade_inicial, qt_idade_final, ds_faixa_idade) AS select	b.nr_seq_faixa_etaria				nr_seq_faixa_etaria,
		b.nr_sequencia					nr_seq_faixa_item,
		a.ds_faixa_etaria				ds_faixa_etaria,
		b.qt_idade_inicial				qt_idade_inicial,
		b.qt_idade_final				qt_idade_final,
		b.qt_idade_inicial || ' a ' || b.qt_idade_final	ds_faixa_idade
	FROM	pls_faixa_etaria 	a,
		pls_faixa_etaria_item	b
	where	a.nr_sequencia 		= b.nr_seq_faixa_etaria
	and	coalesce(a.ie_situacao,'A')	= 'A'
	group by 	b.nr_seq_faixa_etaria,
			b.nr_sequencia,
			a.ds_faixa_etaria,
			b.qt_idade_inicial,
			b.qt_idade_final
	order by 1,2 asc;

