SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW plussoft_inconsist_proposta_v (nr_seq_proposta, cd_inconsistencia, ds_inconsistencia) AS select	a.nr_seq_proposta	nr_seq_proposta,
		a.nr_sequencia cd_inconsistencia,
		coalesce(pls_obter_dados_inconsist_dina(a.nr_seq_inconsistencia,'DS'),pls_obter_dados_inconsist_fixa(a.nr_seq_inconsist_fixa,'DS')) ds_inconsistencia
	FROM  	pls_proposta_validacao a
	where	not exists (select 1 from pls_proposta_check_list b where a.nr_sequencia = b.nr_seq_validacao)
	order by cd_inconsistencia;

