SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW plussoft_incons_proposta_v (nr_sequencia, nr_seq_proposta, nr_seq_inconsistencia, ds_inconsistencia) AS select 	a.nr_sequencia,
		a.nr_seq_proposta,
		a.nr_seq_inconsistencia,
		b.ds_inconsistencia
	FROM   	pls_proposta_validacao a,
		pls_proposta_inconsist b
	where  	b.nr_sequencia = a.nr_seq_inconsistencia
	
union all

	select 	a.nr_sequencia,
		a.nr_seq_proposta,
		a.nr_seq_inconsist_fixa nr_seq_inconsistencia,
		substr(pls_obter_dados_inconsist_fixa(nr_seq_inconsist_fixa,'DS'),1,255)	ds_inconsistencia
	from   	pls_proposta_validacao a
	where  	a.nr_seq_inconsist_fixa is not null
	order by nr_sequencia asc;

