SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW plussoft_servicos_sca_v (nr_contrato, nr_seq_sca, ds_plano, vl_preco_atual) AS select	c.nr_contrato,
		a.nr_seq_plano nr_seq_sca,
		b.ds_plano,
		(select	max(x.vl_preco_atual)
		FROM	pls_plano_preco x
		where	x.nr_seq_tabela = a.nr_seq_tabela) vl_preco_atual
	from	pls_sca_regra_contrato a,
		pls_plano b,
		pls_contrato c
	where	b.nr_sequencia	= a.nr_seq_plano
	and	c.nr_sequencia	= a.nr_seq_contrato
	and 	LOCALTIMESTAMP between coalesce(a.dt_inicio_vigencia, LOCALTIMESTAMP) and coalesce(a.dt_fim_vigencia, LOCALTIMESTAMP)
	group by c.nr_contrato,
		a.nr_seq_plano,
		b.ds_plano,
		a.nr_seq_tabela
	order by 1,2;

