SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW prescr_material_sit_v (nr_atendimento, nr_prescricao, cd_setor_atendimento, nr_sequencia_prescricao, dt_item, dt_conta, dt_entrada_unidade, cd_convenio, cd_categoria, qt_executada, qt_devolvida, qt_saldo, vl_item, nm_usuario, cd_item, qt_prescrita, cd_motivo_baixa, dt_baixa, ds_item, tp_item, ds_tp_item, nm_usuario_original, nm_prescritor, ds_setor_paciente, cd_local_estoque, qt_checados) AS select	/*+ ORDEDED */	p.nr_atendimento, 
	p.nr_prescricao, 
	a.cd_setor_atendimento, 
	a.nr_sequencia_prescricao, 
	p.dt_prescricao		dt_item, 
	a.dt_conta, 
	a.dt_entrada_unidade, 
	a.cd_convenio, 
	a.cd_categoria, 
	a.qt_executada qt_executada, 
	a.qt_devolvida qt_devolvida, 
	a.qt_material qt_saldo, 
	a.vl_material vl_item, 
	i.nm_usuario, 
	i.cd_material cd_item, 
	i.qt_material qt_prescrita, 
	i.cd_motivo_baixa, 
	i.dt_baixa, 
	m.ds_material ds_item, 
	m.ie_tipo_material tp_item	 /* 1-MATERIAL 2-MED.GENERICO 3-MED.COMERC. */
, 
	CASE WHEN m.ie_tipo_material=1 THEN 'MATERIAL'  ELSE 'MEDICAMENTOS' END  ds_tp_item, 
	p.nm_usuario_original, 
	substr(coalesce(obter_nome_pf(p.cd_prescritor),'Não informado'),1,60) nm_prescritor, 
	substr(coalesce(obter_nome_setor(p.cd_setor_atendimento),'Não informado'),1,100) ds_setor_paciente, 
	a.cd_local_estoque, 
	(	select	count(b.dt_fim_horario) 
		FROM	prescr_mat_hor b 
		where	b.dt_fim_horario	is not null 
		and	i.nr_prescricao		= b.nr_prescricao 
		and	i.nr_sequencia		= b.nr_seq_material 
		and	m.cd_material		= b.cd_material 
	) qt_checados 
FROM prescr_medica p, material m, prescr_material i
LEFT OUTER JOIN material_atend_paciente a ON (i.nr_prescricao = a.nr_prescricao AND i.nr_sequencia = a.nr_sequencia_prescricao)
WHERE p.nr_prescricao	= i.nr_prescricao and i.cd_material	= m.cd_material;

