CREATE OR REPLACE VIEW procedimentos_ps_v (nr_seq_item_pront, nr_atendimento, nr_prescricao, nr_seq_prescricao, ds_exame_grid, ie_status_execucao, ie_status_atend, nr_seq_interno, nr_seq_laudo, ie_status_laudo, ie_imagem_pacs, nr_seq_result) AS select	3 nr_seq_item_pront,
	c.nr_atendimento, 
	c.nr_prescricao, 
	a.nr_seq_prescricao, 
	a.ds_titulo_laudo ds_exame_grid, 
	d.ie_status_execucao, 
	null ie_status_atend, 
	d.nr_seq_interno, 
	a.nr_sequencia nr_seq_laudo, 
