SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW processo_longo_v (inst_id, sid, "serial#", nm_procedure, qt_processada, ds_operacao) AS SELECT	INST_ID,
	SID,
	SERIAL#,
	SUBSTR(ACTION,1,position('@' in ACTION)-1) NM_PROCEDURE,
	(CASE WHEN position('@' in ACTION)=0 THEN 0  ELSE SUBSTR(ACTION,position('@' in ACTION)+1,LENGTH(ACTION)) END )::numeric  QT_PROCESSADA,
	CLIENT_INFO	DS_OPERACAO
FROM	GV$SESSION
where	SUBSTR(ACTION,1,position('@' in ACTION)-1) is not null;

