CREATE OR REPLACE VIEW projeto_estudo_v (cd_projeto, ds_projeto, dt_atualizacao, nm_usuario, cd_projeto_superior, dt_atualizacao_nrec, nm_usuario_nrec, nivel) AS WITH RECURSIVE cte AS (
SELECT A.CD_PROJETO,A.DS_PROJETO,A.DT_ATUALIZACAO,A.NM_USUARIO,A.CD_PROJETO_SUPERIOR,A.DT_ATUALIZACAO_NREC,A.NM_USUARIO_NREC,1 NIVEL
 FROM PROJETO_ESTUDO A WHERE A.CD_PROJETO_SUPERIOR IS NULL
  UNION ALL
SELECT A.CD_PROJETO,A.DS_PROJETO,A.DT_ATUALIZACAO,A.NM_USUARIO,A.CD_PROJETO_SUPERIOR,A.DT_ATUALIZACAO_NREC,A.NM_USUARIO_NREC,(c.level+1) NIVEL
 FROM PROJETO_ESTUDO A JOIN cte c ON (c.CD_PROJETO = A.CD_PROJETO_SUPERIOR)

) SELECT * FROM cte;


