CREATE OR REPLACE VIEW quant_cesarias_v (nr_atendimento, qt_parto_cesario, dt_nascimento) AS SELECT	b.nr_atendimento,
	a.QT_PARTO_CESARIO,
	trunc(b.dt_nascimento) dt_nascimento
FROM   	parto a,
	nascimento b
WHERE  	a.nr_atendimento = b.nr_atendimento
AND	QT_PARTO_CESARIO IS NOT NULL
AND	QT_PARTO_CESARIO > 0;

