SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW reaprazar_horarios_html5_v (dt_horario, nr_prescricao, nr_seq_item, ie_tipo_item, dt_suspensao) AS select	b.dt_horario, /*Medicamentos*/

		a.nr_prescricao,
		a.nr_sequencia nr_seq_item,
		'M' ie_tipo_item,
        b.dt_suspensao
FROM	prescr_material		a,
		prescr_mat_hor		b
where	a.nr_prescricao	= b.nr_prescricao
and		a.nr_sequencia	= b.nr_seq_material
and		a.ie_agrupador in (1,2)
and		a.nr_seq_mat_cpoe is not null

union all

select	distinct b.dt_horario, /*Solucoes*/

		a.nr_prescricao,
		a.nr_seq_solucao nr_seq_item,
		'SOL' ie_tipo_item,
        b.dt_suspensao
from	prescr_solucao		a,
		prescr_mat_hor		b
where	a.nr_prescricao		= b.nr_prescricao
and		a.nr_seq_solucao	= b.nr_seq_solucao
and		b.ie_agrupador 		= 4
and		exists (	select	1				
					from	prescr_material x
					where	x.nr_prescricao = a.nr_prescricao
					and		x.nr_sequencia_solucao = a.nr_seq_solucao
					and		x.nr_seq_mat_cpoe is not null)

union all

select	b.dt_horario, /*Procedimentos*/

		a.nr_prescricao,
		a.nr_sequencia nr_seq_item,
		'P' ie_tipo_item,
        b.dt_suspensao
from	prescr_procedimento		a,
		prescr_proc_hor			b
where	a.nr_prescricao		= b.nr_prescricao
and		a.nr_sequencia		= b.nr_seq_procedimento;

