SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW resumo_medico_v (cd_medico_executor, nr_seq_protocolo, nm_medico, vl_medico) AS select 	a.cd_medico_executor,
	b.nr_seq_protocolo, 
    	substr(obter_nome_pf(a.cd_medico_executor),1,100) nm_medico, 
    	sum(vl_procedimento) vl_medico 
FROM  procedimento_paciente a, 
    conta_paciente b 
where a.nr_interno_conta = b.nr_interno_conta 
and  a.cd_medico_executor is not null 
GROUP BY b.nr_seq_protocolo, 
	 a.cd_medico_executor, 
     substr(obter_nome_pf(a.cd_medico_executor),1,100) HAVING sum(vl_procedimento) <> 0
;

