CREATE OR REPLACE VIEW rmv_cirugia (coligada, prontuario, paciente, codespecialidade, tipocirugia, tipoferida, datahorainicio, datahoratermino, asa, crmanestesista, tipoanestesia, codcirurgia) AS select	1 coligada,
	a.nr_prontuario prontuario,
	a.nm_pessoa_fisica paciente,
	null codespecialidade,
	b.ie_carater_cirurgia tipocirugia,
	b.cd_tipo_cirurgia tipoferida,
	b.dt_inicio_prevista datahorainicio,
	b.dt_termino datahoratermino,
	null asa,
	c.nr_crm crmanestesista,
	b.cd_tipo_anestesia tipoanestesia,
	b.nr_cirurgia codcirurgia
FROM pessoa_fisica a, cirurgia b
LEFT OUTER JOIN medico c ON (b.cd_medico_anestesista = c.cd_pessoa_fisica)
WHERE a.cd_pessoa_fisica = b.cd_pessoa_fisica;

