SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW rmv_cirurgioes (crm, funcao, codcirurgia) AS select	b.nr_crm crm,
	'P' funcao,
	a.nr_cirurgia codcirurgia
FROM	medico b,
	cirurgia a
where	a.cd_medico_cirurgiao = b.cd_pessoa_fisica

union

select	b.nr_crm crm,
	'A' funcao,
	a.nr_cirurgia codcirurgia
from	medico b,
	cirurgia a,
	cirurgia_participante c
where	c.cd_pessoa_fisica = b.cd_pessoa_fisica
and	a.nr_cirurgia = c.nr_cirurgia;

