CREATE OR REPLACE VIEW rss_prod_tre_6 (dt_atividade, cd_cargo, cd_setor_usuario, nm_pessoa_usuario_exec, qt_horas_prod, qt_horas, vl_percentual) AS SELECT  a.dt_atividade,
		a.cd_cargo,
		a.cd_setor_usuario,
		nm_pessoa_usuario_exec,
		SUM(a.qt_horas_prod) qt_horas_prod,
		SUM(a.qt_horas) qt_horas,
		(SUM(a.qt_horas_prod) / SUM(a.qt_horas)) * 100 vl_percentual
FROM    RSS_PROD_TRE_5 a
GROUP	BY a.nm_pessoa_usuario_exec,
		   a.dt_atividade,
		   a.cd_cargo,
		   a.cd_setor_usuario;

