SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW san_item_integracao_v (cd, ds, ie_tabela_integ) AS select	b.nr_sequencia cd,
	b.nm_pais ds,
	'PAIS' ie_tabela_integ
FROM	pais b

union all

select	b.nr_sequencia cd,
	b.ds_metodo ds,
	'ME' ie_tabela_integ
from	san_metodo_exame b

union all

select	b.nr_sequencia cd,
	b.ds_motivo_desistencia ds,
	'IC' ie_tabela_integ
from	san_motivo_desistencia b

union all

select	b.nr_sequencia cd,
	b.ds_tipo_doacao ds,
	'TD' ie_tabela_integ
from	san_tipo_doacao b

union all

select	b.nr_sequencia cd,
	(c.ds_exame || ' | ' || b.ds_valor) ds,
	'RES' ie_tabela_integ
from	san_exame_valor b,
	san_exame c
where	b.nr_seq_exame = c.nr_sequencia

union all

select	b.nr_sequencia cd,
	b.ds_impedimento ds,
	'IN' ie_tabela_integ
from	san_impedimento b

union all

select	b.nr_sequencia cd,
	b.ds_anticorpos ds,
	'AC' ie_tabela_integ
from	san_anticorpos b;

