CREATE OR REPLACE VIEW san_item_integ_imp_v (cd, ds, ie_tabela_integ) AS select	substr(obter_desc_expressao_br(cd_exp_valor_dominio), 1, 255) cd,
	substr(obter_desc_expressao(cd_exp_valor_dominio, ds_valor_dominio), 1, 255) ds,
	'RED' ie_tabela_integ
FROM	valor_dominio
where	cd_dominio = 1356

union

select	substr(obter_desc_expressao_br(cd_exp_valor_dominio), 1, 255) cd,
	substr(obter_desc_expressao(cd_exp_valor_dominio, ds_valor_dominio), 1, 255) ds,
	'RED' ie_tabela_integ
from	valor_dominio
where	cd_dominio = 1357

union

select	distinct substr(ds_valor, 1, 255) cd,
	substr(ds_valor, 1, 255) ds,
	'RED' ie_tabela_integ
from	san_exame_valor;

